Scripts.OnLoad:
						self.OppoDirection = false;
						self.TimeSinceLastUpdate = 0
						self.TotalTime = 0;
						self.Index = 1;
						self.Pending = false;
						self.IsPlaying = false;	
						self.Target = self:GetParent().Scroll

						self.SequenceInfo =	{
						["TotalFrames"] = 21,
						["cX"] = 0.1416015625,
						["cY"] = 0.283203125,
						["Column"] = 7,
						["Row"] = 3	
						}

						self.OppoDirection = false;
						self.TimeSinceLastUpdate = 0
						self.TotalTime = 0;
						self.Index = 1;
						self.Pending = false;
						self.IsPlaying = false;	
						self.Target = self:GetParent().AnimCover

						self.SequenceInfo =	{
						["TotalFrames"] = 22,
						["cX"] = 0.146484375,
						["cY"] = 0.185546875,
						["Column"] = 6,
						["Row"] = 5	
						}

Scripts.OnHide:
self.IsPlaying = false;
self.TotalTime = 0;
self.TimeSinceLastUpdate = 0;
self.OppoDirection = not self.OppoDirection;

Scripts.OnClick:
PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
AAA_Journal.Settings:Hide();
local ddm = self:GetParent():GetParent().Filter;
if (not ddm:IsShown() ) then
	ddm:Show();
else
	ddm:Hide();
end

PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
AAA_Journal.Filter:Hide();
local ddm = self:GetParent():GetParent().Settings;
if (not ddm:IsShown() ) then
	ddm:Show();
else
	ddm:Hide();
end

Scripts.OnFinished:
local point, relativeTo, relativePoint, xOfs, yOfs = self:GetParent():GetPoint();
self:GetParent():SetPoint(point, relativeTo, relativePoint, xOfs, yOfs + 30)
self:GetParent().animIn:Play();

Scripts.OnLeave:
self:GetParent().JournalTestBackground.animIn:Stop();
if (not AAA_Journal.VirtualFrame.JournalPinButton.PinButtonDown:IsShown() ) then
	--self:SetAlpha(0);
	UIFrameFadeOut(self,0.15,1,0);
end
GameTooltip:Hide();
self.showingTooltip = false;

Scripts.OnEnter:
if not (self.ListScrollFrame:IsMouseOver() or self.ListScrollFrame2:IsMouseOver() ) then
	SetCursor("Interface/CURSOR/UI-Cursor-Move.blp");
end

Scripts.OnPlay:
self:GetParent():EnableMouse(true)
self:GetParent():Show();
self:GetParent().border.Shadow.animIn:Play();
self:GetParent().image.Background.animIn:Play();
self:GetParent().image.ImageMask.animIn:Play();
self:GetParent().image.Title:Hide();	
self:GetParent().image.SubTitle:Hide();	
self:GetParent().Button1:Hide();
self:GetParent().Button2:Hide();
self:GetParent().Button3:Hide();

Scripts.OnFinished:
self:GetParent().image.Title.animIn:Play();	
if self:GetParent().image.Title:GetText() ~= nil then
self:GetParent().Button1.animIn:Play();
self:GetParent().Button2.animIn:Play();
self:GetParent().Button3.animIn:Play();	
end

Scripts.OnEnter:
				GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT",-20,-100);
				GameTooltip:SetText(MENU_AAA_GRADIENT, 1, 1, 1);
				GameTooltip:AddLine(AAA_MENU_CLICK_TO_OPEN, nil, nil, nil, true);
				GameTooltip:AddLine(AAA_MENU_CLICK_TO_HIDE, nil, nil, nil, true);
				GameTooltip:Show()

				UIFrameFadeIn(self.ShadowGradient,0.15,0,0.6);
				UIFrameFadeIn(self.ColorGradient,0.15,0,1);
				self.ColorGradient.animIn:Play();

				SetCursor("Interface/CURSOR/Inspect.blp");

Scripts.OnLeave:
ResetCursor();
GameTooltip:Hide();
self.ColorGradient.animIn:Pause();
UIFrameFadeOut(self.ColorGradient,0.15,1,0);
if not self.Color:IsShown() then
	UIFrameFadeOut(self.ShadowGradient,0.15,0.6,0);
end

Scripts.OnShow:
if (AAA_Journal.ListScrollFrame2:GetFrameStrata() == "LOW") then
AAA_Journal:SetFrameStrata("MEDIUM");
AAA_Journal.ListScrollFrame2:SetFrameStrata("LOW")
end

if AAA_Journal:IsShown() then
	local point, relativeTo, relativePoint, xOfs, yOfs = AAA_Journal.VirtualFrame.JournalTestBackground:GetPoint();
	AAA_Journal.VirtualFrame.JournalTestBackground:SetPoint(point, relativeTo, relativePoint, xOfs+200, yOfs)
end
NightMode.Border:Hide(); 
AAA_Journal.JournalBottomPanel:SetFrameStrata("LOW")

Scripts.OnHide:
if (AAA_Journal.ListScrollFrame2:GetFrameStrata() == "LOW") then
	AAA_Journal:SetFrameStrata("HIGH");
	AAA_Journal.ListScrollFrame2:SetFrameStrata("LOW")
end
local point, relativeTo, relativePoint, xOfs, yOfs = AAA_Journal.VirtualFrame.JournalTestBackground:GetPoint();
AAA_Journal.VirtualFrame.JournalTestBackground:SetPoint(point, relativeTo, relativePoint, -15, yOfs)
NightMode.Border:Show();
AAA_Journal.JournalBottomPanel:SetFrameStrata("LOW")
